import React, { useState, useRef, useEffect } from 'react';
import { Upload, Camera, AlertCircle, CheckCircle, Loader2, X, Zap, Eye, Target, BarChart3, Download, RefreshCw } from 'lucide-react';

const PCBDefectDetector = () => {
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [detections, setDetections] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [imageDimensions, setImageDimensions] = useState({ width: 640, height: 640 });
  const [showBoundingBoxes, setShowBoundingBoxes] = useState(true);
  const [processingTime, setProcessingTime] = useState(0);
  const fileInputRef = useRef(null);
  const imageRef = useRef(null);

  const defectColors = {
    missing_hole: '#FF6B6B',
    mouse_bite: '#4ECDC4',
    open_circuit: '#45B7D1',
    short: '#FFA07A',
    spur: '#98D8C8',
    spurious_copper: '#F7DC6F'
  };

  const defectDescriptions = {
    missing_hole: 'A hole that should be present in the PCB is missing',
    mouse_bite: 'Small semi-circular notches along the board edges',
    open_circuit: 'Break in the conductive path preventing current flow',
    short: 'Unintended connection between conductors',
    spur: 'Unwanted protrusion of copper material',
    spurious_copper: 'Excess copper material where it shouldn\'t be'
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setImage(e.target.result);
        setImagePreview(e.target.result);
        setDetections([]);
        setError(null);
        setProcessingTime(0);
      };
      reader.readAsDataURL(file);
    }
  };

  const detectDefects = async () => {
    if (!image) return;

    setLoading(true);
    setError(null);
    const startTime = performance.now();

    try {
      const formData = new FormData();
      const blob = await fetch(image).then(res => res.blob());
      formData.append('image', blob, 'pcb_image.png');

      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      const endTime = performance.now();
      setProcessingTime(((endTime - startTime) / 1000).toFixed(2));

      if (response.ok) {
        setDetections(data.predictions || []);
        if (data.image_dimensions) {
          setImageDimensions(data.image_dimensions);
        }
      } else {
        setError(data.error || 'Detection failed');
      }
    } catch (err) {
      console.error(err);
      setError('Failed to connect to the backend. Make sure Flask server is running.');
    } finally {
      setLoading(false);
    }
  };

  const clearImage = () => {
    setImage(null);
    setImagePreview(null);
    setDetections([]);
    setError(null);
    setImageDimensions({ width: 640, height: 640 });
    setProcessingTime(0);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const getBoundingBoxStyle = (bbox, imageElement) => {
    if (!imageElement) return {};

    const imageRect = imageElement.getBoundingClientRect();
    const scaleX = imageRect.width / imageDimensions.width;
    const scaleY = imageRect.height / imageDimensions.height;

    return {
      position: 'absolute',
      left: bbox.x1 * scaleX,
      top: bbox.y1 * scaleY,
      width: (bbox.x2 - bbox.x1) * scaleX,
      height: (bbox.y2 - bbox.y1) * scaleY,
      border: `3px solid ${defectColors[bbox.class] || '#FF0000'}`,
      backgroundColor: `${defectColors[bbox.class] || '#FF0000'}15`,
      borderRadius: '4px',
      pointerEvents: 'none',
      animation: 'pulse 2s infinite'
    };
  };

  const getDefectStats = () => {
    const stats = {};
    detections.forEach(detection => {
      stats[detection.class] = (stats[detection.class] || 0) + 1;
    });
    return stats;
  };

  const downloadReport = () => {
    const stats = getDefectStats();
    const report = {
      timestamp: new Date().toISOString(),
      processingTime: processingTime,
      totalDefects: detections.length,
      defectStats: stats,
      detections: detections.map(d => ({
        type: d.class,
        confidence: d.confidence,
        location: d.bbox
      }))
    };

    const blob = new Blob([JSON.stringify(report, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `pcb_defect_report_${new Date().toISOString().split('T')[0]}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
      <style jsx>{`
        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.7; }
        }

        .glass-effect {
          backdrop-filter: blur(10px);
          background: rgba(255, 255, 255, 0.8);
          border: 1px solid rgba(255, 255, 255, 0.2);
        }

        .defect-card {
          transition: all 0.3s ease;
        }

        .defect-card:hover {
          transform: translateY(-2px);
          box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
        }

        .upload-zone {
          transition: all 0.3s ease;
        }

        .upload-zone:hover {
          transform: scale(1.02);
        }
      `}</style>

      <div className="max-w-7xl mx-auto p-6">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="bg-gradient-to-r from-blue-600 to-purple-600 p-3 rounded-2xl">
              <Zap className="text-white" size={32} />
            </div>
            <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
              PCB Defect Detection System
            </h1>
          </div>
          <p className="text-gray-600 text-lg max-w-2xl mx-auto">
            Advanced AI-powered PCB quality control system. Upload your PCB image to detect manufacturing defects with high precision.
          </p>
        </div>

        {/* Main Content */}
        <div className="grid grid-cols-1 xl:grid-cols-3 gap-6">
          {/* Upload and Image Section */}
          <div className="xl:col-span-2 space-y-6">
            {/* Upload Section */}
            <div className="glass-effect rounded-3xl p-6 shadow-xl">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold text-gray-800 flex items-center gap-2">
                  <Upload className="text-blue-600" size={24} />
                  Image Upload
                </h2>
                {processingTime > 0 && (
                  <div className="text-sm text-gray-600 bg-gray-100 px-3 py-1 rounded-full">
                    Processing time: {processingTime}s
                  </div>
                )}
              </div>

              <div className="flex items-center gap-4 mb-6">
                <label className="upload-zone flex items-center gap-3 bg-gradient-to-r from-blue-600 to-purple-600 text-white px-6 py-3 rounded-2xl hover:from-blue-700 hover:to-purple-700 cursor-pointer transition-all duration-300 shadow-lg">
                  <Camera size={20} />
                  Choose PCB Image
                  <input
                    ref={fileInputRef}
                    type="file"
                    accept="image/*"
                    onChange={handleImageUpload}
                    className="hidden"
                  />
                </label>

                {image && (
                  <button
                    onClick={clearImage}
                    className="flex items-center gap-2 bg-gray-500 text-white px-4 py-3 rounded-2xl hover:bg-gray-600 transition-colors duration-300"
                  >
                    <X size={20} />
                    Clear
                  </button>
                )}

                {detections.length > 0 && (
                  <button
                    onClick={downloadReport}
                    className="flex items-center gap-2 bg-green-600 text-white px-4 py-3 rounded-2xl hover:bg-green-700 transition-colors duration-300"
                  >
                    <Download size={20} />
                    Report
                  </button>
                )}
              </div>

              {error && (
                <div className="flex items-center gap-3 bg-red-50 text-red-700 p-4 rounded-2xl mb-6 border border-red-200">
                  <AlertCircle size={20} />
                  {error}
                </div>
              )}

              {/* Image Display */}
              {imagePreview && (
                <div className="space-y-4">
                  <div className="bg-white rounded-2xl p-4 shadow-inner">
                    <div className="relative inline-block w-full">
                      <img
                        ref={imageRef}
                        src={imagePreview}
                        alt="PCB"
                        className="w-full h-auto rounded-xl shadow-lg"
                        style={{ maxHeight: '500px', objectFit: 'contain' }}
                      />

                      {/* Bounding boxes overlay */}
                      {showBoundingBoxes && detections.map((detection, index) => {
                        const imageElement = imageRef.current;
                        const boxStyle = getBoundingBoxStyle(detection.bbox, imageElement);

                        return (
                          <div key={index} style={boxStyle} className="pointer-events-none">
                            <div 
                              className="absolute -top-8 left-0 text-xs font-bold px-2 py-1 rounded-lg text-white shadow-lg"
                              style={{ backgroundColor: defectColors[detection.class] || '#FF0000' }}
                            >
                              {detection.class ? detection.class.replace('_', ' ') : 'Unknown'} ({(detection.confidence * 100).toFixed(1)}%)
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  </div>

                  {/* Controls */}
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-4">
                      <label className="flex items-center gap-2 text-gray-700">
                        <input
                          type="checkbox"
                          checked={showBoundingBoxes}
                          onChange={(e) => setShowBoundingBoxes(e.target.checked)}
                          className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                        />
                        <Eye size={16} />
                        Show Bounding Boxes
                      </label>
                    </div>

                    <button
                      onClick={detectDefects}
                      disabled={loading}
                      className="flex items-center gap-3 bg-gradient-to-r from-green-600 to-blue-600 text-white px-8 py-3 rounded-2xl hover:from-green-700 hover:to-blue-700 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed transition-all duration-300 shadow-lg font-semibold"
                    >
                      {loading ? (
                        <>
                          <Loader2 className="animate-spin" size={20} />
                          Analyzing...
                        </>
                      ) : (
                        <>
                          <Target size={20} />
                          Detect Defects
                        </>
                      )}
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Results Section */}
          <div className="xl:col-span-1 space-y-6">
            {/* Statistics */}
            <div className="glass-effect rounded-3xl p-6 shadow-xl">
              <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center gap-2">
                <BarChart3 className="text-purple-600" size={20} />
                Detection Summary
              </h3>

              <div className="space-y-3">
                <div className="flex justify-between items-center p-3 bg-gray-50 rounded-xl">
                  <span className="text-gray-700">Total Defects</span>
                  <span className="font-bold text-2xl text-red-600">{detections.length}</span>
                </div>

                {Object.entries(getDefectStats()).map(([defect, count]) => (
                  <div key={defect} className="flex justify-between items-center p-3 bg-gray-50 rounded-xl">
                    <div className="flex items-center gap-2">
                      <div 
                        className="w-3 h-3 rounded-full"
                        style={{ backgroundColor: defectColors[defect] }}
                      />
                      <span className="text-gray-700 capitalize">{defect.replace('_', ' ')}</span>
                    </div>
                    <span className="font-semibold">{count}</span>
                  </div>
                ))}
              </div>
            </div>

            {/* Detection Results */}
            <div className="glass-effect rounded-3xl p-6 shadow-xl">
              <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center gap-2">
                <CheckCircle className="text-green-600" size={20} />
                Detection Results
              </h3>

              {detections.length === 0 ? (
                <div className="text-center py-8">
                  <div className="text-gray-400 mb-2">
                    <Target size={48} className="mx-auto mb-2" />
                    {loading ? 'Analyzing PCB...' : 'No defects detected'}
                  </div>
                  <p className="text-gray-500 text-sm">
                    {loading ? 'Please wait while we process your image' : 'Upload an image and click "Detect Defects" to analyze'}
                  </p>
                </div>
              ) : (
                <div className="space-y-3 max-h-96 overflow-y-auto">
                  {detections.map((detection, index) => (
                    <div
                      key={index}
                      className="defect-card bg-white p-4 rounded-xl border-l-4 shadow-sm"
                      style={{ borderLeftColor: defectColors[detection.class] || '#FF0000' }}
                    >
                      <div className="flex items-start justify-between mb-2">
                        <div className="flex items-center gap-2">
                          <div 
                            className="w-3 h-3 rounded-full"
                            style={{ backgroundColor: defectColors[detection.class] }}
                          />
                          <h4 className="font-semibold text-gray-800 capitalize">
                            {detection.class ? detection.class.replace('_', ' ') : 'Unknown'}
                          </h4>
                        </div>
                        <span className="text-sm font-medium text-gray-600 bg-gray-100 px-2 py-1 rounded-full">
                          {(detection.confidence * 100).toFixed(1)}%
                        </span>
                      </div>

                      <p className="text-sm text-gray-600 mb-2">
                        {defectDescriptions[detection.class] || 'Unknown defect type'}
                      </p>

                      <div className="text-xs text-gray-500 font-mono">
                        Position: ({detection.bbox.x1.toFixed(0)}, {detection.bbox.y1.toFixed(0)}) - 
                        ({detection.bbox.x2.toFixed(0)}, {detection.bbox.y2.toFixed(0)})
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Legend */}
            <div className="glass-effect rounded-3xl p-6 shadow-xl">
              <h4 className="font-semibold text-gray-800 mb-4">Defect Types Legend</h4>
              <div className="space-y-2">
                {Object.entries(defectColors).map(([defect, color]) => (
                  <div key={defect} className="flex items-center gap-3 p-2 hover:bg-gray-50 rounded-lg transition-colors">
                    <div 
                      className="w-4 h-4 rounded-full border-2 border-white shadow-sm"
                      style={{ backgroundColor: color }}
                    />
                    <span className="text-sm capitalize font-medium">
                      {defect.replace('_', ' ')}
                    </span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PCBDefectDetector;
