import React, { useState, useRef } from 'react';
import { Upload, Camera, AlertCircle, CheckCircle, Loader2, X } from 'lucide-react';

const PCBDefectDetector = () => {
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [detections, setDetections] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [imageDimensions, setImageDimensions] = useState({ width: 0, height: 0 });
  const fileInputRef = useRef(null);

  const defectColors = {
    missing_hole: '#FF6B6B',
    mouse_bite: '#4ECDC4',
    open_circuit: '#45B7D1',
    short: '#FFA07A',
    spur: '#98D8C8',
    spurious_copper: '#F7DC6F'
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setImage(e.target.result);
        setImagePreview(e.target.result);
        setDetections([]);
        setError(null);
      };
      reader.readAsDataURL(file);
    }
  };

  const detectDefects = async () => {
    if (!image) return;

    setLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:5000/api/detect', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ image }),
      });

      const data = await response.json();

      if (data.success) {
        setDetections(data.detections);
        setImageDimensions(data.image_dimensions);
      } else {
        setError(data.error || 'Detection failed');
      }
    } catch (err) {
      setError('Failed to connect to the backend. Make sure Flask server is running.');
    } finally {
      setLoading(false);
    }
  };

  const clearImage = () => {
    setImage(null);
    setImagePreview(null);
    setDetections([]);
    setError(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const getBoundingBoxStyle = (bbox) => {
    const scaleX = 400 / imageDimensions.width;
    const scaleY = 300 / imageDimensions.height;

    return {
      position: 'absolute',
      left: bbox.x1 * scaleX,
      top: bbox.y1 * scaleY,
      width: (bbox.x2 - bbox.x1) * scaleX,
      height: (bbox.y2 - bbox.y1) * scaleY,
      border: `2px solid ${defectColors[bbox.class] || '#FF0000'}`,
      backgroundColor: 'transparent',
      pointerEvents: 'none'
    };
  };

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="max-w-6xl mx-auto">
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <h1 className="text-3xl font-bold text-gray-800 mb-2 flex items-center gap-2">
            <Camera className="text-blue-600" />
            PCB Defect Detection System
          </h1>
          <p className="text-gray-600 mb-6">
            Upload a PCB image to detect defects: missing holes, mouse bites, open circuits, shorts, spurs, and spurious copper
          </p>

          {/* Upload Section */}
          <div className="mb-6">
            <div className="flex items-center gap-4 mb-4">
              <label className="flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 cursor-pointer transition-colors">
                <Upload size={20} />
                Upload PCB Image
                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  className="hidden"
                />
              </label>

              {image && (
                <button
                  onClick={clearImage}
                  className="flex items-center gap-2 bg-gray-500 text-white px-4 py-2 rounded-lg hover:bg-gray-600 transition-colors"
                >
                  <X size={20} />
                  Clear
                </button>
              )}
            </div>

            {error && (
              <div className="flex items-center gap-2 bg-red-100 text-red-700 p-3 rounded-lg mb-4">
                <AlertCircle size={20} />
                {error}
              </div>
            )}
          </div>

          {/* Image Preview and Detection */}
          {imagePreview && (
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Image Display */}
              <div className="space-y-4">
                <div className="relative inline-block">
                  <img
                    src={imagePreview}
                    alt="PCB"
                    className="w-full max-w-md h-auto rounded-lg shadow-md"
                    style={{ maxHeight: '400px', objectFit: 'contain' }}
                  />

                  {/* Bounding boxes overlay */}
                  {detections.map((detection, index) => (
                    <div
                      key={index}
                      style={getBoundingBoxStyle(detection.bbox)}
                      className="pointer-events-none"
                    >
                      <div 
                        className="absolute -top-6 left-0 text-xs font-bold px-1 py-0.5 rounded text-white"
                        style={{ backgroundColor: defectColors[detection.class] || '#FF0000' }}
                      >
                        {detection.class} ({(detection.confidence * 100).toFixed(1)}%)
                      </div>
                    </div>
                  ))}
                </div>

                <button
                  onClick={detectDefects}
                  disabled={loading}
                  className="w-full bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors flex items-center justify-center gap-2"
                >
                  {loading ? (
                    <>
                      <Loader2 className="animate-spin" size={20} />
                      Detecting Defects...
                    </>
                  ) : (
                    <>
                      <CheckCircle size={20} />
                      Detect Defects
                    </>
                  )}
                </button>
              </div>

              {/* Detection Results */}
              <div className="space-y-4">
                <h3 className="text-xl font-semibold text-gray-800">
                  Detection Results ({detections.length} defects found)
                </h3>

                {detections.length === 0 ? (
                  <div className="text-gray-500 text-center py-8">
                    {loading ? 'Processing image...' : 'No defects detected or click "Detect Defects" to analyze the image.'}
                  </div>
                ) : (
                  <div className="space-y-3">
                    {detections.map((detection, index) => (
                      <div
                        key={index}
                        className="bg-gray-50 p-4 rounded-lg border-l-4"
                        style={{ borderLeftColor: defectColors[detection.class] || '#FF0000' }}
                      >
                        <div className="flex items-center justify-between mb-2">
                          <h4 className="font-semibold text-gray-800 capitalize">
                            {detection.class.replace('_', ' ')}
                          </h4>
                          <span className="text-sm text-gray-600">
                            {(detection.confidence * 100).toFixed(1)}% confidence
                          </span>
                        </div>
                        <div className="text-sm text-gray-600">
                          Location: ({detection.bbox.x1.toFixed(0)}, {detection.bbox.y1.toFixed(0)}) - 
                          ({detection.bbox.x2.toFixed(0)}, {detection.bbox.y2.toFixed(0)})
                        </div>
                      </div>
                    ))}
                  </div>
                )}

                {/* Legend */}
                <div className="mt-6">
                  <h4 className="font-semibold text-gray-800 mb-3">Defect Types</h4>
                  <div className="grid grid-cols-2 gap-2">
                    {Object.entries(defectColors).map(([defect, color]) => (
                      <div key={defect} className="flex items-center gap-2">
                        <div 
                          className="w-4 h-4 rounded border"
                          style={{ backgroundColor: color }}
                        ></div>
                        <span className="text-sm capitalize">
                          {defect.replace('_', ' ')}
                        </span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default PCBDefectDetector;
