import React, { useState, useRef, useEffect } from 'react';
import { Upload, Camera, AlertCircle, CheckCircle, Loader2, X, Zap, Shield, Eye, Cpu, Activity, Target } from 'lucide-react';

const PCBDefectDetector = () => {
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [detections, setDetections] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [imageDimensions, setImageDimensions] = useState({ width: 640, height: 640 });
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const fileInputRef = useRef(null);

  const defectColors = {
    missing_hole: '#FF6B6B',
    mouse_bite: '#4ECDC4',
    open_circuit: '#45B7D1',
    short: '#FFA07A',
    spur: '#98D8C8',
    spurious_copper: '#F7DC6F'
  };

  const defectIcons = {
    missing_hole: Target,
    mouse_bite: Activity,
    open_circuit: Zap,
    short: AlertCircle,
    spur: Eye,
    spurious_copper: Cpu
  };

  useEffect(() => {
    if (loading) {
      setIsAnalyzing(true);
      const timer = setTimeout(() => setIsAnalyzing(false), 2000);
      return () => clearTimeout(timer);
    }
  }, [loading]);

  const handleImageUpload = (event) => {
    const file = event.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setImage(e.target.result);
        setImagePreview(e.target.result);
        setDetections([]);
        setError(null);
      };
      reader.readAsDataURL(file);
    }
  };

  const detectDefects = async () => {
    if (!image) return;

    setLoading(true);
    setError(null);

    try {
      const formData = new FormData();
      const blob = await fetch(image).then(res => res.blob());
      formData.append('image', blob, 'pcb_image.png');

      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (response.ok) {
        setDetections(data.predictions || []);
        if (data.image_dimensions) {
          setImageDimensions(data.image_dimensions);
        }
      } else {
        setError(data.error || 'Detection failed');
      }
    } catch (err) {
      console.error(err);
      setError('Failed to connect to the backend. Make sure Flask server is running.');
    } finally {
      setLoading(false);
    }
  };

  const clearImage = () => {
    setImage(null);
    setImagePreview(null);
    setDetections([]);
    setError(null);
    setImageDimensions({ width: 640, height: 640 });
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const getBoundingBoxStyle = (bbox, imageElement) => {
    if (!imageElement) return {};

    const imageRect = imageElement.getBoundingClientRect();
    const scaleX = imageRect.width / imageDimensions.width;
    const scaleY = imageRect.height / imageDimensions.height;

    return {
      position: 'absolute',
      left: bbox.x1 * scaleX,
      top: bbox.y1 * scaleY,
      width: (bbox.x2 - bbox.x1) * scaleX,
      height: (bbox.y2 - bbox.y1) * scaleY,
      border: `2px solid ${defectColors[bbox.class] || '#FF0000'}`,
      backgroundColor: 'transparent',
      pointerEvents: 'none'
    };
  };

  const getDefectSeverity = (confidence) => {
    if (confidence > 0.8) return 'high';
    if (confidence > 0.6) return 'medium';
    return 'low';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 relative overflow-hidden">
      {/* Animated Background */}
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(120,119,198,0.1),transparent)] animate-pulse"></div>
      <div className="absolute top-0 left-0 w-full h-full bg-gradient-to-r from-blue-500/5 to-purple-500/5 animate-gradient-x"></div>

      {/* Floating Particles */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            className="absolute w-2 h-2 bg-blue-400/20 rounded-full animate-float"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 5}s`,
              animationDuration: `${3 + Math.random() * 4}s`
            }}
          />
        ))}
      </div>

      <div className="relative z-10 max-w-7xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="text-center mb-12">
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="relative">
              <Shield className="text-6xl text-blue-400 animate-pulse" />
              <div className="absolute inset-0 bg-blue-400/20 rounded-full blur-xl animate-ping"></div>
            </div>
            <h1 className="text-6xl font-bold bg-gradient-to-r from-blue-400 via-purple-400 to-cyan-400 bg-clip-text text-transparent">
              PCB Vision
            </h1>
          </div>
          <p className="text-xl text-gray-300 max-w-2xl mx-auto leading-relaxed">
            Advanced AI-powered PCB defect detection system with real-time analysis and precision diagnostics
          </p>
        </div>

        {/* Main Content */}
        <div className="bg-white/10 backdrop-blur-lg rounded-3xl shadow-2xl p-8 border border-white/20">
          {/* Upload Section */}
          <div className="mb-8">
            <div className="flex items-center justify-center gap-4 mb-6">
              <label className="group relative overflow-hidden bg-gradient-to-r from-blue-600 to-purple-600 text-white px-8 py-4 rounded-2xl hover:from-blue-700 hover:to-purple-700 cursor-pointer transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl">
                <div className="absolute inset-0 bg-white/20 translate-x-[-100%] group-hover:translate-x-[100%] transition-transform duration-700 skew-x-12"></div>
                <div className="relative flex items-center gap-3">
                  <Upload size={24} className="group-hover:rotate-12 transition-transform duration-300" />
                  <span className="font-semibold text-lg">Upload PCB Image</span>
                </div>
                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  className="hidden"
                />
              </label>

              {image && (
                <button
                  onClick={clearImage}
                  className="flex items-center gap-2 bg-red-500/20 text-red-400 px-6 py-4 rounded-2xl hover:bg-red-500/30 transition-all duration-300 border border-red-500/30 hover:border-red-500/50"
                >
                  <X size={20} />
                  Clear
                </button>
              )}
            </div>

            {error && (
              <div className="flex items-center gap-3 bg-red-500/20 text-red-400 p-4 rounded-2xl mb-6 border border-red-500/30 backdrop-blur-sm">
                <AlertCircle size={24} />
                <span className="font-medium">{error}</span>
              </div>
            )}
          </div>

          {/* Image Analysis Section */}
          {imagePreview && (
            <div className="grid grid-cols-1 xl:grid-cols-2 gap-8">
              {/* Image Display */}
              <div className="space-y-6">
                <div className="relative group">
                  <div className="absolute inset-0 bg-gradient-to-r from-blue-500/20 to-purple-500/20 rounded-2xl blur-xl group-hover:blur-2xl transition-all duration-300"></div>
                  <div className="relative bg-gray-900/50 backdrop-blur-sm rounded-2xl p-6 border border-white/10">
                    <div className="relative inline-block w-full">
                      <img
                        id="pcb-image"
                        src={imagePreview}
                        alt="PCB"
                        className="w-full h-auto rounded-xl shadow-2xl max-h-96 object-contain"
                      />

                      {/* Loading Overlay */}
                      {loading && (
                        <div className="absolute inset-0 bg-black/50 backdrop-blur-sm rounded-xl flex items-center justify-center">
                          <div className="text-center">
                            <Loader2 className="animate-spin text-blue-400 mb-4 mx-auto" size={48} />
                            <p className="text-white font-medium">Analyzing PCB...</p>
                          </div>
                        </div>
                      )}

                      {/* Bounding boxes overlay */}
                      {detections.map((detection, index) => {
                        const imageElement = document.getElementById('pcb-image');
                        const boxStyle = getBoundingBoxStyle(detection.bbox, imageElement);

                        return (
                          <div
                            key={index}
                            style={boxStyle}
                            className="pointer-events-none animate-pulse"
                          >
                            <div 
                              className="absolute -top-8 left-0 text-xs font-bold px-2 py-1 rounded-lg text-white shadow-lg"
                              style={{ backgroundColor: defectColors[detection.class] || '#FF0000' }}
                            >
                              {detection.class ? detection.class.replace('_', ' ') : 'Unknown'} ({(detection.confidence * 100).toFixed(1)}%)
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  </div>
                </div>

                <button
                  onClick={detectDefects}
                  disabled={loading}
                  className="w-full bg-gradient-to-r from-green-600 to-emerald-600 text-white px-8 py-4 rounded-2xl hover:from-green-700 hover:to-emerald-700 disabled:from-gray-600 disabled:to-gray-700 disabled:cursor-not-allowed transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl group"
                >
                  <div className="flex items-center justify-center gap-3">
                    {loading ? (
                      <>
                        <Loader2 className="animate-spin" size={24} />
                        <span className="font-semibold text-lg">Analyzing...</span>
                      </>
                    ) : (
                      <>
                        <Eye size={24} className="group-hover:scale-110 transition-transform duration-300" />
                        <span className="font-semibold text-lg">Detect Defects</span>
                      </>
                    )}
                  </div>
                </button>
              </div>

              {/* Detection Results */}
              <div className="space-y-6">
                <div className="bg-gray-900/30 backdrop-blur-sm rounded-2xl p-6 border border-white/10">
                  <h3 className="text-2xl font-bold text-white mb-4 flex items-center gap-3">
                    <Target className="text-blue-400" size={28} />
                    Analysis Results
                    <span className="bg-blue-500/20 text-blue-400 px-3 py-1 rounded-full text-sm font-medium">
                      {detections.length} defects
                    </span>
                  </h3>

                  {detections.length === 0 ? (
                    <div className="text-center py-12">
                      {loading ? (
                        <div className="space-y-4">
                          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-400 mx-auto"></div>
                          <p className="text-gray-400">Processing image...</p>
                        </div>
                      ) : (
                        <div className="space-y-4">
                          <CheckCircle className="text-green-400 mx-auto" size={48} />
                          <p className="text-gray-400">No defects detected or click "Detect Defects" to analyze</p>
                        </div>
                      )}
                    </div>
                  ) : (
                    <div className="space-y-4 max-h-96 overflow-y-auto custom-scrollbar">
                      {detections.map((detection, index) => {
                        const severity = getDefectSeverity(detection.confidence);
                        const DefectIcon = defectIcons[detection.class] || AlertCircle;

                        return (
                          <div
                            key={index}
                            className="bg-gray-800/50 backdrop-blur-sm p-4 rounded-xl border-l-4 hover:bg-gray-800/70 transition-all duration-300 group"
                            style={{ borderLeftColor: defectColors[detection.class] || '#FF0000' }}
                          >
                            <div className="flex items-center justify-between mb-3">
                              <div className="flex items-center gap-3">
                                <div 
                                  className="p-2 rounded-lg"
                                  style={{ backgroundColor: `${defectColors[detection.class] || '#FF0000'}20` }}
                                >
                                  <DefectIcon 
                                    size={20} 
                                    style={{ color: defectColors[detection.class] || '#FF0000' }}
                                  />
                                </div>
                                <h4 className="font-semibold text-white capitalize text-lg">
                                  {detection.class ? detection.class.replace('_', ' ') : 'Unknown'}
                                </h4>
                              </div>
                              <div className="text-right">
                                <div className="text-sm text-gray-400 mb-1">Confidence</div>
                                <div className="font-bold text-white">
                                  {(detection.confidence * 100).toFixed(1)}%
                                </div>
                              </div>
                            </div>

                            <div className="flex items-center gap-4 text-sm text-gray-400">
                              <span>
                                Position: ({detection.bbox.x1.toFixed(0)}, {detection.bbox.y1.toFixed(0)})
                              </span>
                              <span 
                                className={`px-2 py-1 rounded-full text-xs font-medium ${
                                  severity === 'high' ? 'bg-red-500/20 text-red-400' :
                                  severity === 'medium' ? 'bg-yellow-500/20 text-yellow-400' :
                                  'bg-green-500/20 text-green-400'
                                }`}
                              >
                                {severity.toUpperCase()}
                              </span>
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  )}
                </div>

                {/* Legend */}
                <div className="bg-gray-900/30 backdrop-blur-sm rounded-2xl p-6 border border-white/10">
                  <h4 className="font-bold text-white mb-4 flex items-center gap-2">
                    <Cpu className="text-purple-400" size={20} />
                    Defect Types
                  </h4>
                  <div className="grid grid-cols-2 gap-3">
                    {Object.entries(defectColors).map(([defect, color]) => {
                      const DefectIcon = defectIcons[defect] || AlertCircle;
                      return (
                        <div key={defect} className="flex items-center gap-3 p-2 rounded-lg hover:bg-white/5 transition-colors duration-200">
                          <div className="flex items-center gap-2">
                            <div 
                              className="w-3 h-3 rounded-full border-2"
                              style={{ backgroundColor: color, borderColor: color }}
                            />
                            <DefectIcon size={16} style={{ color }} />
                          </div>
                          <span className="text-sm text-gray-300 capitalize">
                            {defect.replace('_', ' ')}
                          </span>
                        </div>
                      );
                    })}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-20px) rotate(180deg); }
        }

        @keyframes gradient-x {
          0%, 100% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
        }

        .animate-float {
          animation: float 6s ease-in-out infinite;
        }

        .animate-gradient-x {
          animation: gradient-x 15s ease infinite;
          background-size: 400% 400%;
        }

        .custom-scrollbar::-webkit-scrollbar {
          width: 6px;
        }

        .custom-scrollbar::-webkit-scrollbar-track {
          background: rgba(255, 255, 255, 0.1);
          border-radius: 10px;
        }

        .custom-scrollbar::-webkit-scrollbar-thumb {
          background: rgba(59, 130, 246, 0.5);
          border-radius: 10px;
        }

        .custom-scrollbar::-webkit-scrollbar-thumb:hover {
          background: rgba(59, 130, 246, 0.8);
        }
      `}</style>
    </div>
  );
};

export default PCBDefectDetector;
